import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import Chart from './components/Chart';

class App extends Component {
  constructor() {
    super();
    this.state = {
      chartData: {}
    }
  }

  // life cycle hook, will run when the components mounted/loads
  componentWillMount(){
    this.getChartData();
  }

  getChartData() {
    // AJAX calls here
    this.setState({
      chartData: {
        labels: ['January', 'February', 'March', 'April', 'May', 'June'],
        datasets: [
            {
                label: 'Population',
                data: [
                    6.17,
                    1.81,
                    1.53,
                    1.06,
                    1.05,
                    9.5
                ],
                // backgroundColor: 'darkslateblue',
                borderColor:'darkslateblue',
                fill: false
            },

            {
              label: 'Density',
              data: [
                  4.17,
                  5.81,
                  -2.53,
                  4.06,
                  3.05,
                  8.5
              ],
             // backgroundColor: 'darkslateblue',
             borderColor:'deeppink',
             fill: false
          },

          {
            label: 'Weight',
            data: [
                8.17,
                10.81,
                4.53,
                2.06,
                9.05,
                3.5
            ],
           // backgroundColor: 'darkslateblue',
           borderColor:'turquoise',
           fill: false
        }
        ]
    }
    });
  }

render() {
  return (
    <div className="App">
        <img src={logo} className="App-logo" alt="logo" />
        <Chart chartData={this.state.chartData} location='Massachusetts' 
        legendPosition="top"/>
    </div>
    );
  }
}

export default App;
