import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import Chart from './components/Chart';

class App extends Component {
  constructor() {
    super();
    this.state = {
      chartData: {}
    }
  }

  // life cycle hook, will run when the components mounted/loads
  componentWillMount(){
    this.getChartData();
  }

  getChartData() {
    // AJAX calls here
    this.setState({
      chartData: {
        labels: ['January', 'February', 'March', 'April', 'May', 'June'],
        datasets: [
            {
                label: 'Population',
                data: [
                    617594,
                    181045,
                    153060,
                    106519,
                    105162,
                    95072
                ],
                // backgroundColor: 'darkslateblue',
                borderColor:'darkslateblue',
                fill: false
            },

            {
              label: 'Density',
              data: [
                  417594,
                  581045,
                  -253060,
                  406519,
                  305162,
                  85072
              ],
             // backgroundColor: 'darkslateblue',
             borderColor:'deeppink',
             fill: false
          },

          {
            label: 'Weight',
            data: [
                817594,
                1081045,
                453060,
                206519,
                905162,
                35072
            ],
           // backgroundColor: 'darkslateblue',
           borderColor:'turquoise',
           fill: false
        }
        ]
    }
    });
  }

render() {
  return (
    <div className="App">
        <img src={logo} className="App-logo" alt="logo" />
        <Chart chartData={this.state.chartData} location='Massachusetts' 
        legendPosition="top"/>
    </div>
    );
  }
}

export default App;
